import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const OAuthRedirect = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAccessToken = async (authorizationCode) => {
      try {
        const clientId = "YOUR_CLIENT_ID"; // Replace with your Fitbit Client ID
        const clientSecret = "YOUR_CLIENT_SECRET"; // Replace with your Fitbit Client Secret
        const redirectUri = "http://localhost:3000/oauth-redirect";

        const response = await axios.post(
          "https://api.fitbit.com/oauth2/token",
          new URLSearchParams({
            client_id: clientId,
            grant_type: "authorization_code",
            redirect_uri: redirectUri,
            code: authorizationCode,
          }),
          {
            headers: {
              Authorization: `Basic ${btoa(`${clientId}:${clientSecret}`)}`,
              "Content-Type": "application/x-www-form-urlencoded",
            },
          }
        );

        const { access_token } = response.data;
        console.log("Access Token:", access_token);

        // Save the token locally (use secure storage in production)
        localStorage.setItem("fitbit_access_token", access_token);

        // Redirect to dashboard
        navigate("/dashboard");
      } catch (error) {
        console.error("Error fetching access token:", error);
      }
    };

    const query = new URLSearchParams(window.location.search);
    const authorizationCode = query.get("code");

    if (authorizationCode) {
      fetchAccessToken(authorizationCode);
    } else {
      console.error("No authorization code found in URL");
    }
  }, [navigate]);

  return <div>Authorizing...</div>;
};

export default OAuthRedirect;
