import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [activityData, setActivityData] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const accessToken = localStorage.getItem("fitbit_access_token");

    if (!accessToken) {
      // Als er geen access token is, stuur de gebruiker terug naar de login
      navigate("/");
      return;
    }

    const fetchActivityData = async () => {
      try {
        const response = await axios.get(
          "https://api.fitbit.com/1/user/-/activities/date/today.json",
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );
        setActivityData(response.data.summary);
      } catch (error) {
        console.error("Error fetching activity data:", error.response?.data || error.message);
        // Verwijder ongeldig access token en stuur de gebruiker terug naar de login
        localStorage.removeItem("fitbit_access_token");
        navigate("/");
      } finally {
        setLoading(false);
      }
    };

    fetchActivityData();
  }, [navigate]);

  if (loading) {
    return <div>Loading your data...</div>;
  }

  return (
    <div>
      <h1>FitBuddy Dashboard</h1>
      {activityData ? (
        <div>
          <p>Steps Today: {activityData.steps}</p>
          <p>Calories Burned: {activityData.caloriesOut}</p>
        </div>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default Dashboard;
