import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const OAuthRedirect = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAccessToken = async (authorizationCode) => {
      try {
        // Verstuur de authorization code naar de backend
        const response = await axios.post("http://localhost:4000/fitbit/token", {
          code: authorizationCode, // Verstuur de code naar de backend
        });

        const { access_token } = response.data;

        // Sla het access token op in localStorage
        localStorage.setItem("fitbit_access_token", access_token);

        // Stuur de gebruiker door naar het Dashboard
        navigate("/dashboard");
      } catch (error) {
        console.error("Error fetching access token:", error.response?.data || error.message);
        navigate("/"); // Terug naar login bij fout
      }
    };

    const query = new URLSearchParams(window.location.search);
    const authorizationCode = query.get("code");

    if (authorizationCode) {
      fetchAccessToken(authorizationCode);
    } else {
      console.error("Geen authorization code gevonden in de URL");
      navigate("/");
    }
  }, [navigate]);

  return <div>Bezig met verwerken van je login...</div>;
};

export default OAuthRedirect;
