import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const OAuthRedirect = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAccessToken = async (authorizationCode) => {
      try {
        const clientId = "23PYYF"; // Jouw Client ID
        const clientSecret = "cf4bc2be037b25da39fdaf8cc10b7f9b"; // Jouw Client Secret
        const redirectUri = "http://localhost:3000/oauth-redirect"; // Moet exact overeenkomen met je Developer Portal

        const response = await axios.post(
          "https://api.fitbit.com/oauth2/token",
          new URLSearchParams({
            client_id: clientId,
            grant_type: "authorization_code",
            redirect_uri: redirectUri,
            code: authorizationCode,
          }),
          {
            headers: {
              Authorization: `Basic ${btoa(`${clientId}:${clientSecret}`)}`,
              "Content-Type": "application/x-www-form-urlencoded",
            },
          }
        );

        const { access_token } = response.data;

        console.log("Access Token:", access_token);

        // Sla het access token op in localStorage
        localStorage.setItem("fitbit_access_token", access_token);

        // Stuur de gebruiker door naar de Dashboard-pagina
        navigate("/dashboard");
      } catch (error) {
        console.error("Error fetching access token:", error.response?.data || error.message);
        // Foutmelding? Terug naar login.
        navigate("/");
      }
    };

    // Haal de authorization code uit de URL
    const query = new URLSearchParams(window.location.search);
    const authorizationCode = query.get("code");

    if (authorizationCode) {
      fetchAccessToken(authorizationCode);
    } else {
      console.error("Geen authorization code gevonden in de URL");
      navigate("/"); // Terug naar login
    }
  }, [navigate]);

  return <div>Bezig met inloggen...</div>;
};

export default OAuthRedirect;
