import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [activityData, setActivityData] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchActivityData = async () => {
      const accessToken = localStorage.getItem("fitbit_access_token");

      // Als er geen token is, terug naar login
      if (!accessToken) {
        console.warn("Geen toegangstoken gevonden, terug naar login...");
        navigate("/");
        return;
      }

      try {
        // Verstuur het access token naar de backend
        const response = await axios.post("http://localhost:4000/fitbit/activity", {
          access_token: accessToken,
        });

        setActivityData(response.data.summary);
      } catch (error) {
        console.error("Error fetching activity data:", error.response?.data || error.message);

        // Ongeldig token? Verwijder het en stuur de gebruiker terug naar login
        localStorage.removeItem("fitbit_access_token");
        navigate("/");
      } finally {
        setLoading(false);
      }
    };

    fetchActivityData();
  }, [navigate]);

  if (loading) {
    return <div>Bezig met laden...</div>;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>FitBuddy Dashboard</h1>
      {activityData ? (
        <div>
          <p>Stappen vandaag: {activityData.steps}</p>
          <p>Verbrande calorieÃ«n: {activityData.caloriesOut}</p>
          <p>Actieve minuten: {activityData.veryActiveMinutes}</p>
        </div>
      ) : (
        <p>Geen gegevens beschikbaar.</p>
      )}
    </div>
  );
};

export default Dashboard;
