import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [activityData, setActivityData] = useState(null);
  const [heartRateData, setHeartRateData] = useState(null);
  const [sleepData, setSleepData] = useState(null);
  const [loading, setLoading] = useState(true);

  const accessToken = localStorage.getItem("fitbit_access_token");

  useEffect(() => {
    const fetchFitbitData = async () => {
      try {
        setLoading(true);

        // Fetch activity data
        const activityResponse = await axios.get(
          "https://api.fitbit.com/1/user/-/activities/date/today.json",
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );
        setActivityData(activityResponse.data.summary);

        // Fetch heart rate data
        const heartRateResponse = await axios.get(
          "https://api.fitbit.com/1/user/-/activities/heart/date/today/1d.json",
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );
        setHeartRateData(heartRateResponse.data["activities-heart"][0]);

        // Fetch sleep data
        const sleepResponse = await axios.get(
          "https://api.fitbit.com/1.2/user/-/sleep/date/today.json",
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );
        setSleepData(sleepResponse.data.summary);
      } catch (error) {
        console.error("Error fetching Fitbit data:", error);
      } finally {
        setLoading(false);
      }
    };

    if (accessToken) {
      fetchFitbitData();
    }
  }, [accessToken]);

  if (loading) {
    return <div>Loading your Fitbit stats...</div>;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>FitBuddy Dashboard</h1>

      {/* Activity Data */}
      {activityData && (
        <div>
          <h2>Activity</h2>
          <p>Steps Today: {activityData.steps}</p>
          <p>Calories Burned: {activityData.caloriesOut}</p>
          <p>Active Minutes: {activityData.veryActiveMinutes}</p>
        </div>
      )}

      {/* Heart Rate Data */}
      {heartRateData && (
        <div>
          <h2>Heart Rate</h2>
          <p>Resting Heart Rate: {heartRateData.value.restingHeartRate}</p>
        </div>
      )}

      {/* Sleep Data */}
      {sleepData && (
        <div>
          <h2>Sleep</h2>
          <p>Total Sleep: {sleepData.totalMinutesAsleep} minutes</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
