import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [activityData, setActivityData] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const accessToken = localStorage.getItem("fitbit_access_token");

    // Als er geen token is, terug naar login
    if (!accessToken) {
      console.warn("Geen toegangstoken gevonden, terug naar login...");
      navigate("/");
      return;
    }

    const fetchActivityData = async () => {
      try {
        // Vraag data op bij Fitbit API
        const response = await axios.get(
          "https://api.fitbit.com/1/user/-/activities/date/today.json",
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );
        setActivityData(response.data.summary);
      } catch (error) {
        console.error("Fout bij ophalen activiteitendata:", error.response?.data || error.message);

        // Als het token ongeldig is, verwijderen en terugsturen naar login
        if (error.response?.status === 401) {
          console.warn("Ongeldig toegangstoken, uitloggen...");
          localStorage.removeItem("fitbit_access_token");
          navigate("/");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchActivityData();
  }, [navigate]);

  if (loading) {
    return <div>Bezig met laden...</div>;
  }

  return (
    <div>
      <h1>FitBuddy Dashboard</h1>
      {activityData ? (
        <div>
          <p>Stappen vandaag: {activityData.steps}</p>
          <p>Verbrande calorieÃ«n: {activityData.caloriesOut}</p>
        </div>
      ) : (
        <p>Geen gegevens beschikbaar.</p>
      )}
    </div>
  );
};

export default Dashboard;
